import React from 'react';
import axios from "axios";
import './styles/Form.css';

function Form(){
    const types=["Domestic Abuse","Armed Assault","Public Nuisance"];
    const gettypes=types.map(type=>{
        return(<option value={type}>{type}</option>)});

        const [formData, setFormData] = React.useState(
            {
                name: "",
                jurisdiction: "",
                type: "",
                number: 0,
                description: ""
            }
        )

            console.log(formData);

        function handleChange(event){
            setFormData(prevFormData =>{
                return{
                    ...prevFormData,
                    [event.target.name]:event.target.value
                }
            })
        }

    const onSubmit = (data) => {
            console.log("Data : ");
            console.log(data);
            axios.post("http://localhost:3001",data).then((response) => {
            });
  };



    return(
        <div>
        <form className='form' onSubmit={onSubmit(formData)}>
        <label>Name</label><br/>
        <input type="text" name="name" value={formData.name} onChange={handleChange}></input><br/><br/>
        <label>Jurisdiction</label><br/>
        <input type="text" name="jurisdiction" value={formData.jurisdiction} onChange={handleChange}></input><br/><br/><br/>
        <select name="type" id="type" value={formData.type} onChange={handleChange}>
        <option>Type</option>
        {gettypes}
        </select><br/><br/>
        <label>Number</label><br/>
        <input type="number" name="number" value={formData.number} onChange={handleChange}></input><br/><br/>
        <label>Description</label><br/>
        <textarea name="description" value={formData.description} onChange={handleChange}></textarea><br/><br/>
        <input type='submit'></input><br/><br/>
        </form>
        </div>
    );
}

export default Form;
